import React, { useState } from "react";
import { makeStyles, createStyles, Theme } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import CircularProgress from "@material-ui/core/CircularProgress";
import styled from "styled-components";
import { Link, BrowserRouter as Router } from "react-router-dom";

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      "& > *": {
        margin: theme.spacing(2),
      },
    },
  })
);

interface Report {
  1: number;
  2: number;
  3: number;
  4: number;
}

interface ComponentState {
  link: string;
  report: Report;
}

const initialState = {
  link: "",
  report: {
    1: 0,
    2: 0,
    3: 0,
    4: 0,
  },
};

const Home = () => {
  const classes = useStyles();
  const [isLoading, setLoading] = useState<boolean>(false);
  const [showReport, setShowReport] = useState<boolean>(false);
  const [error, setError] = useState<boolean>(false);
  const [report, setReport] = useState<ComponentState>(initialState);

  const handleGenerateButton = async () => {
    let data = {};
    const url = "http://127.0.0.1:5000/generate-pdf";
    try {
      setLoading(true);
      const response = await fetch(url);
      const res = await response.json();
      const [report, link] = res;
      setReport((preState: ComponentState)=> ({...preState, link, report }));
      setLoading(false);
    } catch (error) {
      setError(true);
      setLoading(false);
    }
  };

  return (
    <Container>
      <Router>
        <Content className={classes.root}>
          <SpinnerContainer>
            <CircularProgress />
          </SpinnerContainer>

          <Button
            color="primary"
            onClick={handleGenerateButton}
            variant="contained"
          >
            Generate
          </Button>
          <ExtendedLink to="#">Link: </ExtendedLink>
          <Button color="primary" variant="contained">
            Report
          </Button>
          <div>
            <p>
              AlphaBetical String: <Span>20</Span>
            </p>
            <p>
              Real Numbers: <Span>20</Span>
            </p>
            <p>
              Integers: <Span>20</Span>
            </p>
            <p>
              AlphaNumerics: <Span>20</Span>
            </p>
          </div>
        </Content>
      </Router>
    </Container>
  );
};

const Container = styled.div`
  border-radius: 10px;
  box-shadow: 5px 10px 20px #888888;
  display: flex;
  flex-direction: column;
  justify-content: space-around;
  position: relative;
  width: 30%;
`;

const Content = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: flex-start;
`;

const ExtendedLink = styled(Link)`
  color: #2596be;
  text-align: center;
`;

const Span = styled.span`
  font-size: 20px;
  font-weight: bold;
`;

const SpinnerContainer = styled.div`
  display: flex;
  justify-content: center;
  margin: 10px 0px;
  width: 100%;
`;

export default Home;
